#include <fstream>
#include <string>
#include <string_view>
#include <vector>

#include "error.h"
#include "json.h"
#include "galactic_market.h"
#include "regional_market.h"

void galactic_market_t::read_from_file(std::istream& file)
{
    
    // Get the number of characters in the input file.
    if (!file.good())
        throw error_message_t(error_code_t::FILE_SIZE_FAILED, "Error.  Failed to determine file size when decoding galactic_market_t object.\n");
    file.seekg(0, std::ios_base::end);
    unsigned file_size = file.tellg();
    file.seekg(0, std::ios_base::beg);
    
    // Read the entire file into RAM at once
    std::string buffer(file_size, '\0');
    file.read(buffer.data(), file_size);
    if (!file.good())
        throw error_message_t(error_code_t::FILE_READ_FAILED, "Error.  Failed to read file when decoding galactic_market object.\n");
    this->read_from_buffer(std::string_view(buffer));
    
}

void galactic_market_t::read_from_buffer(std::string_view buffer)
{
    
    Json::CharReaderBuilder builder;
    Json::CharReader* reader = builder.newCharReader();
    
    Json::Value json_root;
    std::string error_message;
    bool success = reader->parse(buffer.begin(), buffer.end(), &json_root, &error_message);
    delete reader;
    if (!success)
        Json::throwRuntimeError(error_message);
    
    // Now that the JSON syntax is parsed, extract the stat_list specific
    // data.
    this->read_from_json(json_root);
    
}

void galactic_market_t::read_from_json(const Json::Value& json_root)
{
    
    // Parse root
    if (!json_root.isObject())
        throw error_message_t(error_code_t::JSON_SCHEMA_VIOLATION, "Error.  Root of galactic_market is not of type \"object\".\n");
    
    // Decode regions array
    const Json::Value& json_regions = json_root["regions"];
    if (!json_regions.isArray())
        throw error_message_t(error_code_t::JSON_SCHEMA_VIOLATION, "Error.  <galactic_market>/regions was not found or not of type \"array\".\n");
    
    // Decode each regional market in the regions array
    this->regions_.clear();
    this->regions_.reserve(json_regions.size());
    for (const Json::Value& json_cur_region : json_regions)
    {
        regional_market_t cur_regional_market;
        cur_regional_market.read_from_json(json_cur_region);
        this->regions_.emplace(cur_regional_market.region_id(), cur_regional_market);
    }
    
}

void galactic_market_t::write_to_file(std::ostream& file, unsigned indent_start, unsigned spaces_per_tab) const
{
    file << this->write_to_buffer(indent_start, spaces_per_tab);
    if (!file.good())
        throw error_message_t(error_code_t::FILE_WRITE_FAILED, "Error.  Failed to write file when encoding galactic_market_t object.");
}

void galactic_market_t::write_to_buffer(std::string& buffer, unsigned indent_start, unsigned spaces_per_tab) const
{
    
    std::string indent_2(indent_start + 2 * spaces_per_tab, ' ');
    std::string_view indent_1(indent_2.data(), indent_start + spaces_per_tab);
    std::string_view indent_0(indent_2.data(), indent_start);
    
    // It is recommended not to start a new line before the opening brace, to
    // enable chaining.
    buffer += "{\n";
    
    // Encode each regional_market in the regions member.
    buffer += indent_1;
    unsigned num_regions = this->regions_.size();
    if (num_regions == 0)
    {
        
        buffer += "\"regions\": []\n";
        
    } else {
        
        buffer += "\"regions\": [\n";
        buffer += indent_2;
        unsigned num_regions_processed = 0;
        unsigned last_region_to_process = num_regions - 1;
        for (const std::pair<uint64_t, regional_market_t>& cur_pair : this->regions_)
        {
            
            const regional_market_t& cur_regional_market = cur_pair.second;
            
            cur_regional_market.write_to_buffer(buffer, indent_start + 2 * spaces_per_tab, spaces_per_tab);
            
            if (num_regions_processed == last_region_to_process)
                buffer += '\n';
            else
                buffer += ", ";
            num_regions_processed++;
            
        }
        
        buffer += indent_1;
        buffer += "]\n";
        
    }
    
    // It is recommended to not put a newline on the last brace to allow
    // comma chaining when this object is an element of an array.
    buffer += indent_0;
    buffer += '}';
    
}

std::istream& operator>>(std::istream& stream, galactic_market_t& destination)
{
    try
    {
        destination.read_from_file(stream);
    } catch (error_message_t error) {
        stream.setstate(std::ios::failbit);
        throw error;
    } catch (Json::Exception error) {
        stream.setstate(std::ios::failbit);
        throw error;
    }
    return stream;
}

