void station_attribute_t::decode(std::istream& file)
{
    
    // Get the number of characters in the input file.
    if (!file.good())
        throw error_message_t(error_code_t::FILE_SIZE_FAILED);
    file.seekg(0, std::ios_base::end);
    unsigned file_size = file.tellg();
    file.seekg(0, std::ios_base::beg);
    
    // Read the entire file into RAM at once
    std::string buffer(file_size, '\0');
    file.read(buffer.data(), file_size);
    if (!file.good())
        throw error_message_t(error_code_t::FILE_READ_FAILED);
    this->decode(std::string_view(buffer));
    
}

void station_attribute_t::decode(std::string_view buffer)
{
    
    Json::CharReaderBuilder builder;
    Json::CharReader* reader = builder.newCharReader();
    
    Json::Value json_root;
    std::string error_message;
    bool success = reader->parse(buffer.begin(), buffer.end(), &json_root, &error_message);
    delete reader;
    if (!success)
        Json::throwRuntimeError(error_message);
    
    // Now that the JSON syntax is parsed, extract the stat_list specific
    // data.
    this->decode(json_root);
    
}

void station_attribute_t::decode(const Json::Value& json_root)
{
    
    // Parse root
    if (!json_root.isObject())
        throw error_message_t(error_code_t::JSON_SCHEMA_VIOLATION, "Error.  Root of station_attribute_t is not of type \"object\".\n");
    
    // Decode id
    const Json::Value& json_id = json_root["id"];
    if (!json_id.isUInt())
    {
        std::string message("Error.  <station_attribute>/id was not found or not of type \"unsigned int\".\n");
        throw error_message_t(error_code_t::JSON_SCHEMA_VIOLATION, message);
    }
    this->id_ = json_id.asUInt();
    
    // Decode name
    const Json::Value& json_name = json_root["name"];
    if (!json_name.isString())
    {
        std::string message("Error.  <station_attribute>/name was not found or not of type \"string\".\n");
        throw error_message_t(error_code_t::JSON_SCHEMA_VIOLATION, message);
    }
    this->name_ = json_name.asString();
    
    // Decode system
    const Json::Value& json_system = json_root["system"];
    if (!json_system.isUInt())
    {
        std::string message("Error.  <station_attribute>/system was not found or not of type \"unsigned int\".\n");
        throw error_message_t(error_code_t::JSON_SCHEMA_VIOLATION, message);
    }
    this->system_ = json_system.asUInt();
    
    // Decode region
    const Json::Value& json_region = json_root["region"];
    if (!json_region.isUInt())
    {
        std::string message("Error.  <station_attribute>/region was not found or not of type \"unsigned int\".\n");
        throw error_message_t(error_code_t::JSON_SCHEMA_VIOLATION, message);
    }
    this->region_ = json_region.asUInt();
    
}

void station_attribute_t::encode(std::ostream& file, unsigned indent_start, unsigned spaces_per_tab) const
{
    file << this->encode(indent_start, spaces_per_tab);
    if (!file.good())
        throw error_message_t(error_code_t::FILE_WRITE_FAILED);
}

void station_attribute_t::encode(std::string& buffer, unsigned indent_start, unsigned spaces_per_tab) const
{
    
    std::string indent_1(indent_start + 1 * spaces_per_tab, ' ');
    std::string_view indent_0(indent_1.data(), indent_start);
    
    // It is recommended not to start a new line before the opening brace, to
    // enable chaining.
    buffer += "{\n";
    
    // Encode id
    buffer += indent_1;
    buffer += "\"id\": ";
    buffer += std::to_string(this->id_);
    buffer += ",\n";
    
    // Encode name
    buffer += indent_1;
    buffer += "\"name\": \"";
    buffer += escape_quotes(this->name_);
    buffer += "\"\n";
    
    // Encode system
    buffer += indent_1;
    buffer += "\"system\": ";
    buffer += std::to_string(this->system_);
    buffer += ",\n";
    
    // Encode region
    buffer += indent_1;
    buffer += "\"region\": ";
    buffer += std::to_string(this->region_);
    buffer += '\n';
    
    // It is recommended to not put a newline on the last brace to allow
    // comma chaining when this object is an element of an array.
    buffer += indent_0;
    buffer += '}';
    
}

std::istream& operator>>(std::istream& stream, station_attribute_t& destination)
{
    try
    {
        destination.decode(stream);
    } catch (station_attribute_t::error_message_t error) {
        stream.setstate(std::ios::failbit);
        throw error;
    } catch (Json::Exception error) {
        stream.setstate(std::ios::failbit);
        throw error;
    }
    return stream;
}

