void station_attribute_t::read_from_json_file(std::istream& file)
{
    
    // Get the number of characters in the input file.
    if (!file.good())
        throw error_message_t(error_code_t::FILE_SIZE_FAILED, "Error.  Failed to determine file size when decoding station_attribute_t object.\n");
    file.seekg(0, std::ios_base::end);
    unsigned file_size = file.tellg();
    file.seekg(0, std::ios_base::beg);
    
    // Read the entire file into RAM at once
    std::string buffer(file_size, '\0');
    file.read(buffer.data(), file_size);
    if (!file.good())
        throw error_message_t(error_code_t::FILE_READ_FAILED, "Error.  Failed to read file when decoding station_attribute_t object.\n");
    this->read_from_json_buffer(std::string_view(buffer));
    
}

void station_attribute_t::read_from_json_buffer(std::string_view buffer)
{
    
    Json::CharReaderBuilder builder;
    Json::CharReader* reader = builder.newCharReader();
    
    Json::Value json_root;
    std::string error_message;
    bool success = reader->parse(buffer.begin(), buffer.end(), &json_root, &error_message);
    delete reader;
    if (!success)
        Json::throwRuntimeError(error_message);
    
    // Now that the JSON syntax is parsed, extract the stat_list specific
    // data.
    this->read_from_json_json(json_root);
    
}

void station_attribute_t::read_from_json_json(const Json::Value& json_root)
{
    
    // Parse root
    if (!json_root.isObject())
        throw error_message_t(error_code_t::JSON_SCHEMA_VIOLATION, "Error.  Root of station_attribute_t is not of type \"object\".\n");
    
    // Decode name
    const Json::Value& json_name = json_root["name"];
    if (!json_name.isString())
    {
        std::string message("Error.  <station_attribute>/name was not found or not of type \"string\".\n");
        throw error_message_t(error_code_t::JSON_SCHEMA_VIOLATION, message);
    }
    this->name_ = json_name.asString();
    
    // Decode id
    const Json::Value& json_station_id = json_root["station_id"];
    if (!json_station_id.isUInt64())
    {
        std::string message("Error.  <station_attribute>/station_id was not found or not of type \"unsigned int\".\n");
        throw error_message_t(error_code_t::JSON_SCHEMA_VIOLATION, message);
    }
    this->station_id_ = json_station_id.asUInt64();
    
    // Decode system
    const Json::Value& json_system_id = json_root["system_id"];
    if (!json_system_id.isUInt64())
    {
        std::string message("Error.  <station_attribute>/system_id was not found or not of type \"unsigned int\".\n");
        throw error_message_t(error_code_t::JSON_SCHEMA_VIOLATION, message);
    }
    this->system_id_ = json_system_id.asUInt64();
    
    // Decode region
    const Json::Value& json_region_id = json_root["region_id"];
    if (!json_region_id.isUInt64())
    {
        std::string message("Error.  <station_attribute>/region_id was not found or not of type \"unsigned int\".\n");
        throw error_message_t(error_code_t::JSON_SCHEMA_VIOLATION, message);
    }
    this->region_id_ = json_region_id.asUInt64();
    
}

void station_attribute_t::write_to_json_file(std::ostream& file, unsigned indent_start, unsigned spaces_per_tab) const
{
    file << this->write_to_json_buffer(indent_start, spaces_per_tab);
    if (!file.good())
        throw error_message_t(error_code_t::FILE_WRITE_FAILED, "Error.  Failed to write file when encoding station_attribute_t object.");
}

void station_attribute_t::write_to_json_buffer(std::string& buffer, unsigned indent_start, unsigned spaces_per_tab) const
{
    
    std::string indent_1(indent_start + 1 * spaces_per_tab, ' ');
    std::string_view indent_0(indent_1.data(), indent_start);
    
    // It is recommended not to start a new line before the opening brace, to
    // enable chaining.
    buffer += "{\n";
    
    // Encode name
    buffer += indent_1;
    buffer += "\"name\": \"";
    buffer += escape_quotes(this->name_);
    buffer += "\"\n";
    
    // Encode id
    buffer += indent_1;
    buffer += "\"station_id\": ";
    buffer += std::to_string(this->station_id_);
    buffer += ",\n";
    
    // Encode system
    buffer += indent_1;
    buffer += "\"system_id\": ";
    buffer += std::to_string(this->system_id_);
    buffer += ",\n";
    
    // Encode region
    buffer += indent_1;
    buffer += "\"region_id\": ";
    buffer += std::to_string(this->region_id_);
    buffer += '\n';
    
    // It is recommended to not put a newline on the last brace to allow
    // comma chaining when this object is an element of an array.
    buffer += indent_0;
    buffer += '}';
    
}

std::istream& operator>>(std::istream& stream, station_attribute_t& destination)
{
    try
    {
        destination.read_from_json_file(stream);
    } catch (error_message_t error) {
        stream.setstate(std::ios::failbit);
        throw error;
    } catch (Json::Exception error) {
        stream.setstate(std::ios::failbit);
        throw error;
    }
    return stream;
}

